mapper.insert:
INSERT INTO car(
	car_name, car_kind, car_pay, zone_num, car_image
)VALUES(
	#{carName}, #{carKind}, #{carPay}, #{zoneNum}, #{carImage}
)

mapper.update:
UPDATE car SET
	car_name=#{carName}, car_kind=#{carKind}, car_pay=#{carPay}, zone_num=#{zoneNum}
	
	
		
			, car_image=null
		
		
			,car_image=#{carImage}
		
	
	
	WHERE id=#{id}
